sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
 var Project = g_form.getValue('project');
    // Clear all of the choices from the What needed field choice list
    g_form.clearOptions('role');
    // If the the value of the Project field is Bell, Below are the options provided

    if (newValue == 'Bell') {
        g_form.addOption('role', 'ROL', 'ROL');
        g_form.addOption('role', 'SSE', 'Senior Developer');
        g_form.addOption('role', 'Dev', 'Developer');
    }
    // If the the value of the Project field is TalkTalk, Below are the options provided

    if (newValue == 'TalkTalk') {
        g_form.addOption('role', 'Dev', 'Developer');
        g_form.addOption('role', 'Architect', 'Architect');
        g_form.addOption('role', 'Tester', 'Tester');
    }
   // If the the value of the Project field is Tiksu, Below are the options provided
    if (newValue == 'Tiksu') {
        g_form.addOption('role', 'ScrumMaster', 'ScrumMaster');
        g_form.addOption('role', 'Developer', 'Developer');
        g_form.addOption('role', 'ROL', 'ROL');
    }
    // If the form is loading and it is not a new record, set the u_what_needed value to the
    // value from the record before it was loaded
    if (isLoading && !g_form.isNewRecord()) {
        g_form.setValue('role', role);
    }
}
